mapper.select:
SELECT * FROM (SELECT a.*, rownum AS rnum FROM ( SELECT id,free_num,free_context, create_date,update_date,view_rate, free_like,title,bo_del_yn, notice,comment_count FROM free_board a where 1=1
and bo_del_yn != 'Y'
ORDER BY free_num DESC ) a ) WHERE rnum between #{paging.firstRow} and #{paging.lastRow}

mapper.update:
UPDATE free_board
SET title=#{title}
  , free_context = #{freeContext}
WHERE free_Num = #{freeNum}

UPDATE free_board
SET view_rate = view_rate + 1
WHERE free_num = #{freeNum}
