mapper.update:
UPDATE member
SET
    mem_pass   =#{memPass}
  , mem_name   =#{memName}
  , mem_bir    =#{memBir}
  , mem_zip    =#{memZip}
  , mem_add1   =#{memAdd1}
  , mem_add2   =#{memAdd2}
  , mem_hp     =#{memHp}
  , mem_mail   =#{memMail}
  , mem_job    =#{memJob}
  , mem_hobby  =#{memHobby}
WHERE mem_id = #{memId}

mapper.insert:
INSERT INTO member (
    id,
    name,
    password,
    address,
    phnum,
    admin,
    email,
    birth
) VALUES (
            #{memId}
            #{memName}
            #{memPass}
            #{memAdd}
            #{memHp}
            #{isAdmin}
            #{memMail}
            #{memBir}
         );
