mapper.insert:
INSERT INTO user_plan ( id
                      , place_num
                      , place_name
                      , xlab
                      , ylab
                      , place_address
                      , start_date
                      , end_date
                      , day_count
                      , place_load_address
                      , plan_hp
                      , plan_title)
VALUES (
        #{id}
       , place_num_seq.nextval
       , #{placeName}
       , #{xlab}, #{ylab}, #{placeAddress}, #{startDate}
       , #{endDate}, #{dayCount}, #{placeLoadAddress}, #{planHp}
       , #{planTitle}
       )

mapper.delete:
DELETE
FROM plan
WHERE
    id = #{id}
AND
    plan_title = #{title}

mapper.select:
SELECT
    id , place_name , xlab
     , ylab , place_address , start_date
     , end_date , day_count , place_load_address , plan_hp , plan_title
FROM
    user_plan
WHERE id = #{id}
AND plan_title = #{title}
AND day_count = #{dayCount}
AND delete_yn = 'Y'

mapper.update:
UPDATE user_plan
SET delete_yn = 'N'
WHERE id = #{id}
  AND plan_title = #{title}
