mapper.delete:
delete
from plan
WHERE id = #{id}
  AND plan_title = #{title}

mapper.select:
SELECT id , place_name , xlab , ylab , place_address , start_date , end_date , day_count , place_load_address , plan_hp , plan_title FROM user_plan WHERE id = #{id} AND plan_title = #{title} AND delete_yn = 'Y'
AND day_count = #{dayCount}
order by place_num asc

mapper.update:
UPDATE user_plan
SET delete_yn = 'N'
WHERE id = #{id}
  AND plan_title = #{title}

UPDATE user_plan
SET share_id = #{shareId}
WHERE id = #{id}
  AND plan_title = #{title}
