                       take_hp, take_mail, take_add,
                       take_detail_add, take_auto_add, take_req)
VALUES ( #{userId}, SAVE_CART_SEQ.nextval, #{takeName}
       , #{takeHp}, #{takeMail}, #{takeAdd}
       , #{takeDetailAdd}, #{takeAutoAdd}, #{takeReq})
