mapper.insert:
INSERT INTO share_plan ( id
                       , plan_title
                       , share_id
                       , del_yn)
VALUES (
        #{id}
       , #{planTitle}
       , #{shareId}
       , 'Y'
       )

mapper.update:
UPDATE share_plan
SET
    DEL_YN = 'N'
WHERE
    id = #{id}
  AND plan_title = #{title}
  AND share_id = #{shareId}
  AND del_yn = 'Y'

mapper.select:
SELECT COUNT(*)
FROM SHARE_PLAN
WHERE ID = #{id}
  AND PLAN_TITLE = #{planTitle}
  AND SHARE_ID = #{shareId}
  AND DEL_YN = 'Y'
